    private void buttonCadastroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonCadastroActionPerformed
        //Campos formatados vazios abaixo de foneTx , dataInicTx , dataTermTx            
        if(nomeTx.getText().toString().equals("") || enderecoTx.getText().toString().equals("") || foneTx.getText().toString().equals("(  )         ")  || emailTx.getText().toString().equals("") || numeroBrvTx.getText().toString().equals("") || registroTx.getText().toString().equals("") || anoConTx.getText().toString().equals("") || nInstTx.getText().toString().equals("") || cidadeTx.getText().toString().equals("") || ufTx.getText().toString().equals("") || dataInicTx.getText().toString().equals("  /  /    ")  || dataTermTx.getText().toString().equals("  /  /    ")  || descricaoTx.getText().toString().equals("") || empresaTx.getText().toString().equals("")|| cidadeProfTx.getText().toString().equals("") || estadoTx.getText().toString().equals("") || modeloTx.getText().toString().equals("") || capacidadeTx.getText().toString().equals("") || hTx.getText().toString().equals("")){
            JOptionPane.showMessageDialog(this, "Preencha todos os campos para prosseguir!", "ERRO", JOptionPane.ERROR_MESSAGE);
        }else{
                try{
                    //////////////////////////////////////
                    
                    if(!adicionando){
                        expP.setCidadeProf(cidadeProfTx.getText());
                        expP.setDataDeInicio(dataInicTx.getText());
                        expP.setDataDeTermino(dataTermTx.getText());
                        expP.setEmpresa(empresaTx.getText());
                        expP.setEstado(estadoTx.getText());
                        expP.setDescricaoDaAtividade(descricaoTx.getText());
                        
                        expProfissional.add(expP);
                    }
                    
                    if(!adicionando2){
                        tda.setModelo(modeloTx.getText());
                        tda.setCapacidade(Integer.parseInt(capacidadeTx.getText()));
                        tda.setHorasDevoo(Integer.parseInt(hTx.getText()));
                        
                        tAeronave.add(tda);
        
                    }
                    
                    
                    piloto = new Aviador(nomeTx.getText(),enderecoTx.getText(),foneTx.getText(),emailTx.getText(),Long.parseLong(numeroBrvTx.getText()),Long.parseLong(registroTx.getText()),Integer.parseInt(anoConTx.getText()),nInstTx.getText(),cidadeTx.getText(),ufTx.getText(), expProfissional,tAeronave);
                    
                    //Exceções
                    if(Long.parseLong(numeroBrvTx.getText())<0){
                        throw new InteiroNegativo(Long.parseLong(numeroBrvTx.getText()));
                    }
                    if(Long.parseLong(registroTx.getText())<0){
                        throw new InteiroNegativo(Long.parseLong(registroTx.getText()));
                    }
                    if(Integer.parseInt(anoConTx.getText())<0){
                        throw new InteiroNegativo(Integer.parseInt(anoConTx.getText()));
                    }
                    if(Integer.parseInt(capacidadeTx.getText())<0){
                        throw new InteiroNegativo(Integer.parseInt(capacidadeTx.getText()));
                    }
                    if(Integer.parseInt(hTx.getText())<0){
                        throw new InteiroNegativo(Integer.parseInt(hTx.getText()));
                    }
                    AppMenu.metodoMenu(piloto,1, true); //chamando metodo criado no App Menu para executar a funçao do switch case(1) que no caso é para verificar se existe dados ja cadastrados de um determinado piloto
                    if(AppMenu.Verificado()){
                        
                        JOptionPane.showMessageDialog(this, "Dados Cadastrados");                   
                    }
                }catch(NumberFormatException e){
                        JOptionPane.showMessageDialog(this, "Dados invalidos , digite valores numéricos para prosseguir !", "ERRO", JOptionPane.ERROR_MESSAGE);
                }
                catch(InteiroNegativo e){
                        JOptionPane.showMessageDialog(this, e.toString());
                }
            }
    }//GEN-LAST:event_buttonCadastroActionPerformed

