    private void AdicionarButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AdicionarButton1ActionPerformed
        adicionando = false;
        // Pegando informações dos campos
        if(cidadeTx.getText().toString().equals("") || ufTx.getText().toString().equals("") || dataInicTx.getText().toString().equals("  /  /    ")  || dataTermTx.getText().toString().equals("  /  /    ")  || descricaoTx.getText().toString().equals("") || empresaTx.getText().toString().equals("")|| cidadeProfTx.getText().toString().equals("") || estadoTx.getText().toString().equals("")){
            JOptionPane.showMessageDialog(this, "Preencha todos os campos para prosseguir!", "ERRO", JOptionPane.ERROR_MESSAGE);
        }
        else{
            
        expP.setCidadeProf(cidadeProfTx.getText());
        expP.setDataDeInicio(dataInicTx.getText());
        expP.setDataDeTermino(dataTermTx.getText());
        expP.setEmpresa(empresaTx.getText());
        expP.setEstado(estadoTx.getText());
        expP.setDescricaoDaAtividade(descricaoTx.getText());
        
        
        // Adicionando ao ArrayList
        expProfissional.add(expP);
        JOptionPane.showMessageDialog(null, "Inserido com sucesso!");
        //Zerando a variável para adicionar outros
        expP = new ExperienciaProfissional();
        }
    }//GEN-LAST:event_AdicionarButton1ActionPerformed

    private void Adicionar2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_Adicionar2ActionPerformed
        // TODO add your handling code here:
        adicionando2 = false;
        // Pegando informações dos campos
        if(modeloTx.getText().toString().equals("") || capacidadeTx.getText().toString().equals("") || hTx.getText().toString().equals("")){
            JOptionPane.showMessageDialog(this, "Preencha todos os campos para prosseguir!", "ERRO", JOptionPane.ERROR_MESSAGE);
        }
        else{
            try{
                    tda.setModelo(modeloTx.getText());
                    tda.setCapacidade(Integer.parseInt(capacidadeTx.getText()));
                    tda.setHorasDevoo(Integer.parseInt(hTx.getText()));
        
                    //Exceções
                    if(Integer.parseInt(capacidadeTx.getText())<0){
                        throw new InteiroNegativo(Integer.parseInt(capacidadeTx.getText()));
                    }
                    if(Integer.parseInt(hTx.getText())<0){
                        throw new InteiroNegativo(Integer.parseInt(hTx.getText()));
                    }
                    // Adicionando ao ArrayList
                    else{
                    tAeronave.add(tda);
                    JOptionPane.showMessageDialog(null, "Inserido com sucesso!");
                    //Zerando a variável para adicionar outros
                    tda = new TipoDeAeronave();
                    }
                }catch(NumberFormatException e){
                        JOptionPane.showMessageDialog(this, "Dados invalidos , digite valores numéricos para prosseguir !", "ERRO", JOptionPane.ERROR_MESSAGE);
                }
                catch(InteiroNegativo e){
                        JOptionPane.showMessageDialog(this, e.toString());
                }
        }
    }//GEN-LAST:event_Adicionar2ActionPerformed

